import React, { useState } from 'react';
import './Login.css';


function Login({ onLogin, onGoToRegister }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showReset, setShowReset] = useState(false);
  const [resetUsername, setResetUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [resetMsg, setResetMsg] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const ok = onLogin(username, password);
    if (!ok) {
      setError('Kullanıcı adı veya şifre hatalı!');
    }
  };

  const handleReset = (e) => {
    e.preventDefault();
    if (!resetUsername || !newPassword) {
      setResetMsg('Kullanıcı adı ve yeni şifre girin.');
      return;
    }
    // Kullanıcıyı localStorage'dan bul ve şifresini değiştir
    const users = JSON.parse(localStorage.getItem('users') || '[]');
    const idx = users.findIndex(u => u.username === resetUsername);
    if (idx === -1) {
      setResetMsg('Kullanıcı bulunamadı!');
      return;
    }
    users[idx].password = newPassword;
    localStorage.setItem('users', JSON.stringify(users));
    setResetMsg('Şifre başarıyla değiştirildi. Giriş yapabilirsiniz.');
    setShowReset(false);
  };

  return (
    <div className="login-container">
      {!showReset ? (
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>Giriş Yap</h2>
          <input
            type="text"
            placeholder="Kullanıcı Adı"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Şifre"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Giriş</button>
          <button type="button" style={{marginLeft:8,background:'none',color:'#2d6cdf',border:'none',cursor:'pointer',textDecoration:'underline'}} onClick={() => setShowReset(true)}>
            Şifremi Unuttum
          </button>
          {error && <div className="login-error">{error}</div>}
          <div style={{textAlign:'center',marginTop:8}}>
            <span>Hesabın yok mu? </span>
            <button type="button" style={{background:'none',color:'#2d6cdf',border:'none',cursor:'pointer',textDecoration:'underline'}} onClick={onGoToRegister}>Kayıt Ol</button>
          </div>
        </form>
      ) : (
        <form className="login-form" onSubmit={handleReset}>
          <h2>Şifre Sıfırla</h2>
          <input
            type="text"
            placeholder="Kullanıcı Adı"
            value={resetUsername}
            onChange={e => setResetUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Yeni Şifre"
            value={newPassword}
            onChange={e => setNewPassword(e.target.value)}
            required
          />
          <button type="submit">Şifreyi Sıfırla</button>
          <button type="button" style={{marginLeft:8,background:'none',color:'#2d6cdf',border:'none',cursor:'pointer',textDecoration:'underline'}} onClick={() => setShowReset(false)}>
            Geri Dön
          </button>
          {resetMsg && <div className="login-error">{resetMsg}</div>}
        </form>
      )}
    </div>
  );
}

export default Login;
