import React, { useState } from 'react';
import './Login.css';

function Register({ onRegister, onGoToLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Tüm alanları doldurun.');
      return;
    }
    if (password !== confirm) {
      setError('Şifreler eşleşmiyor!');
      return;
    }
    onRegister(username, password);
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Kayıt Ol</h2>
        <input
          type="text"
          placeholder="Kullanıcı Adı"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Şifre Tekrar"
          value={confirm}
          onChange={(e) => setConfirm(e.target.value)}
          required
        />
        <button type="submit">Kayıt Ol</button>
        {error && <div className="login-error">{error}</div>}
        <div style={{textAlign:'center',marginTop:8}}>
          <span>Hesabın var mı? </span>
          <button type="button" style={{background:'none',color:'#2d6cdf',border:'none',cursor:'pointer',textDecoration:'underline'}} onClick={onGoToLogin}>Giriş Yap</button>
        </div>
      </form>
    </div>
  );
}

export default Register;
